import util from '../vendors/util.js';
import lang from '../vendors/lang.js';
import ChapterStar from '../components/ChapterStar.vue';
import BottomPanel from '../components/BottomPanel.vue';
import NavBar from '../components/NavBar.vue';

export default {
  name: 'detail',
  data() {
    return {
      chapterid: parseInt(util.getQuery('chapterid')),
      bookid: parseInt(util.getQuery('bookid')),
      chapterNum: parseInt(util.getQuery('chapternum')) + 1,
      pageList: [],
      userLang: {},
      chapterIdArr: [],
      chapterNumArr: [],
      isToBottom: false
    };
  },
  // computed: {
  //   currChapternum() {
  //     return parseInt(this.chapterNum) + 1;
  //   },
  // },
  created() {
    this.getChapterList();
    this.getBookInfo();
    //获取用户设备语言 包含常规浏览器和ie浏览器
    let langKey = (navigator.language || navigator.userLanguage).slice(0, 2);
    //切换ui至相应语言
    this.userLang = lang[langKey];
  },
  mounted() {
    //动态设置fixed元素的宽度
    let dom = document.getElementsByClassName('toggle-chapter')[0];
    dom.style.width = document.body.clientWidth + 'px';
    // 调用统计章节pv函数
    this.detailPvHandle();

    window.addEventListener('scroll', this.handleScroll, true);
  },
  methods: {
    /**
     * 统计阅读页pv函数,章节id可从链接获取,ChapterStar.vue有获取cookie uuid工具函数
     */
    detailPvHandle() {
      console.log('detail pv');
    },
    /**
     * 统计完成章节阅读用户
     */
    chapterDoneHandle() {
      console.log('chapter done');
    },
    handleScroll() {
      //判断页面是否拉到底部
      let scrollDom = this.$refs.container;
      let distance =
        scrollDom.scrollHeight -
        document.body.scrollTop -
        document.body.clientHeight;
      if (distance < 700 && !this.isToBottom) {
        // 在距离底部400px之内,调用统计完成章节阅读用户函数
        this.chapterDoneHandle();
        console.log(new Date())
        this.isToBottom = true
      }
    },
    /**
     * 获取章节详情
     */
    getChapterList() {
      const getChapterDetail =
        '//previewapi.raymangaapp.com/previewapi/v1/common/getChapterDetail';

      this.$axios
        .post(getChapterDetail, {
          book_id: parseInt(this.bookid),
          chapter_id: parseInt(this.chapterid),
        })
        .then(res => {
          /**
           * 状态码
           * 1     ：成功
           * 2000  ：常规错误
           * 2001  ：参数错误
           * 2002  ：数据库连接错误
           */
          const { code, pageList } = res.data;
          if (code === 1) {
            this.pageList = pageList;
          } else if (code === 2000) {
            this.$toast('常规错误(2000)');
          } else if (code === 2001) {
            this.$toast('参数错误(2001)');
          } else if (code === 2002) {
            this.$toast('数据库连接错误(2002)');
          }
        })
        .catch(error => {
          this.$toast('网络繁忙，请稍后再试');
        });
    },
    /**
     * 获取chapterid arr
     */
    getBookInfo() {
      const getBookInfo =
        '//previewapi.raymangaapp.com/previewapi/v1/common/getBookInfo';
      this.$axios
        .post(getBookInfo, {
          book_id: this.bookid,
        })
        .then(res => {
          const { code, bookInfo } = res.data;
          if (code === 1) {
            // 本书章节id数组
            bookInfo.chapter_list.forEach(item => {
              this.chapterIdArr.push(item.chapter_id);
              this.chapterNumArr.push(item.chapter_number);
            });
            console.log(this.chapterNumArr);
          }
        })
        .catch(error => {
          this.$toast('网络繁忙，请稍后再试');
        });
    },
    /**
     * 切换章节
     */

    prevChapter() {
      let currChapterIdx = this.chapterIdArr.indexOf(this.chapterid);
      if (currChapterIdx === 0) return;
      //按章节ID切换章节
      this.chapterid = this.chapterIdArr[currChapterIdx - 1];
      this.chapterNum = this.chapterNumArr[currChapterIdx - 1]+ 1;
      this.getChapterList();
      //切换章节时滚动视口到顶部
      document.body.scrollTop = 0;
    },
    nextChapter() {
      let currChapterIdx = this.chapterIdArr.indexOf(this.chapterid);
      let chapterLen = this.chapterIdArr.length;
      if (currChapterIdx >= chapterLen - 1) return;

      this.chapterid = this.chapterIdArr[currChapterIdx + 1];
      this.chapterNum = this.chapterNumArr[currChapterIdx + 1] + 1;
      this.getChapterList();
      document.body.scrollTop = 0;
    },
  },
  components: {
    BottomPanel,
    ChapterStar,
    NavBar,
  },
};
